INSERT [dbo].[Competition$] ([id], [title], [date_start], [date_end], [description], [city]) VALUES (13, N'Региональный чемпионат «Молодые профессионалы» (WorldSkills Russia) Республики Татарстан 2019', N'2019-11-13', N'2020-04-06', N'<p>С 13 ноября 2019 года по 6 февраля 2020 - го в Республике Татарстан прошёл Региональный чемпионат &laquo;Молодые профессионалы&raquo; (WorldSkills Russia) чемпионатного цикла 2019/2020 годов.</p>
