INSERT [dbo].[Skills$] ([id], [title], [skill_block_id], [is_future], [description], [площадь на рабочее место (кв#м)], [площадь комнаты оценки на одну экспертную группу (кв# м)], [зона брифинга (кв# м)], [площадь склада (кв# м)], [F10], [количество участников в команде]) VALUES (13, N'Стоматология ортопедическая', 5, 0, N'<p>Ортопедическая стоматология &ndash; это область стоматологии, которая фокусируется на протезировании зубов в целях улучшения функциональности и эстетики. Ортопедическая стоматология предлагает множество процедур, начиная с таких базовых процедур, как пломбы, коронки и отбеливание зубов, и до самых сложных, таких как протезирование при отсутствии большого количества зубов, лечение зубной эрозии и сложных нарушений функции челюсти.</p>
<p>Современный зубной техник должен воплотить все замыслы врача-ортопеда, а для этого должен обладать хорошим художественным вкусом, абстрактным мышлением, хорошими мануальными навыками, а также знать сложные современные технологии изготовления ортопедических конструкций.</p>', 3, 9, 39, 7, 10, 1)
