INSERT [dbo].[Skills$] ([id], [title], [skill_block_id], [is_future], [description], [площадь на рабочее место (кв#м)], [площадь комнаты оценки на одну экспертную группу (кв# м)], [зона брифинга (кв# м)], [площадь склада (кв# м)], [F10], [количество участников в команде]) VALUES (15, N'3D моделирование для компьютерных игр', 6, 0, N'<p>Профессионал в области 3D моделирования изучает задачу, и, комбинируя дизайнерскую концепцию, креативность и свои технические навыки, материализует задумку заказчика в виде трёхмерных моделей. Области работы специалистов: компьютерные игры, кинематограф, анимационные фильмы, виртуальная реальность. Специалист должен владеть прототипированием и созданием дизайн-концепта, трёхмерным моделированием объектов, созданием UV-развёртки, текстурированием модели, риггингом и анимацией.</p>
<p>&nbsp;</p>
<p><strong>Название</strong><strong> ENG: 3D Digital Game Art </strong></p>
