INSERT [dbo].[Skills$] ([id], [title], [skill_block_id], [is_future], [description], [площадь на рабочее место (кв#м)], [площадь комнаты оценки на одну экспертную группу (кв# м)], [зона брифинга (кв# м)], [площадь склада (кв# м)], [F10], [количество участников в команде]) VALUES (19, N'Управление автогрейдером', 7, 0, N'<p>Машинист автогрейдера &ndash; это квалифицированный рабочий, выполняющий механизированные работы с применением автогрейдера в условиях строительства, обслуживания и ремонта автомобильных дорог, аэродромов, гидротехнических и других сооружений в соответствии со строительными нормами и правилами, а также отвечает за техническое обслуживание и хранение автогрейдера.</p>
<p>Специалист должен обладать знаниями в области востребованных дорожно-строительной отраслью технологий выполнения дорожно-строительных работ. Кроме того, для выполнения работ необходима физическая сила и выносливость, высокая острота слуха и зрения, высокая скорость и точность сенсомоторных реакций, развитый глазомер (линейный, плоскостной, объемный), умение распределять и быстро переключать внимание, хорошая зрительная память, склонность к работе с техникой, техническое мышление, хорошая пространственная ориентация, способность предвидеть развитие дорожных ситуаций, наблюдательность и терпение, дисциплинированность и ответственность.</p>', 2, 9, 15, 8, 1, 3)
