INSERT [dbo].[Skills$] ([id], [title], [skill_block_id], [is_future], [description], [площадь на рабочее место (кв#м)], [площадь комнаты оценки на одну экспертную группу (кв# м)], [зона брифинга (кв# м)], [площадь склада (кв# м)], [F10], [количество участников в команде]) VALUES (23, N'Разработка мобильных приложений', 1, 1, N'<p>Смартфоны продаются и продажи только растут. Компании всё больше вкладывают в развитие мобильных технологий, благодаря чему потребители из просто пользователей плавно превращаются в тех, кто использует мобильные устройства везде и всегда: на работе, дома, на отдыхе, что приводит к важным последствиям для компаний-разработчиков мобильных приложений. Спрос на mobile-программистов, способных выполнять множество профессиональных задач, растёт высокими темпами, а подходящих профессионалов на рынке сейчас появляется крайне мало. Основная задача специалиста по разработке мобильных приложений &ndash; создание мобильного приложения, сочетающего в себе такие обязательные качества, как безотказная работа на одной из мобильных операционных платформ (Apple iOS, Google Android, Windows Phone), удобный пользовательский интерфейс, многофункциональность.</p>
<p><strong>&nbsp;</strong></p>
<p><strong>Название</strong><strong> ENG: Mobile application development </strong></p>
<p><strong>Статус компетенции: Основная </strong></p>
<p><strong>Тип: RU </strong></p>
<p><strong>№ CIS: F6 </strong></p>
<p><strong>Количество конкурсантов в команде: 1</strong></p>', 2, 7, 22, 8, 10, 3)
