INSERT [dbo].[Skills$] ([id], [title], [skill_block_id], [is_future], [description], [площадь на рабочее место (кв#м)], [площадь комнаты оценки на одну экспертную группу (кв# м)], [зона брифинга (кв# м)], [площадь склада (кв# м)], [F10], [количество участников в команде]) VALUES (82, N'Каркасное домостроение', 4, 0, N'<p>Сборщик каркасных домов &ndash; специалист, осуществляющий полный цикл работ по возведению каркасного дома, а также его внутренней и внешней отделке. Специалист должен знать основы строительной физики и технических измерений, правила чтения строительных чертежей, способы устройства каркасов стен, чистых обшивок и временных сооружений, устройство и последовательность сборки конструкций стен, перекрытий и кровель. Он также должен уметь выполнять нарезку деталей в размер для конструкций стен, перекрытий и кровли и их механическую фиксацию, применять различные технологии по укладке и установке различных герметизирующих, несущих и отделочных материалов. Специалист умеет размечать, подгонять, закреплять элементы ендовы, ребра карнизного свеса, парапетов и других сложных или уникальных элементов, относящихся к каркасному домостроению и многое другое.</p>', 4, 10, 22, 9, 9, 1)
