INSERT [dbo].[Skills$] ([id], [title], [skill_block_id], [is_future], [description], [площадь на рабочее место (кв#м)], [площадь комнаты оценки на одну экспертную группу (кв# м)], [зона брифинга (кв# м)], [площадь склада (кв# м)], [F10], [количество участников в команде]) VALUES (91, N'Лабораторный медицинский анализ', 5, 0, N'<p>Сегодня лабораторная диагностика &ndash; это широкий спектр автоматизированных высокочувствительных методов, позволяющий оперативно получить достоверную информацию о состоянии внутренней среды пациента.</p>
<p>Медицинский лабораторный техник клинико-диагностической, гистологической, бактериологической, санитарно-гигиенической лабораторий выполняет исследования в соответствии с квалификационными требованиями, подготавливает рабочее место, дезинфицирующие растворы, регистрирует поступающий в лабораторию биологический материал для исследования, в том числе с использованием персонального компьютера, проводит обработку материала и подготовку к исследованию, стерилизацию лабораторного инструментария в соответствии с действующими инструкциями, ведет необходимую медицинскую документацию, соблюдает правила техники безопасности и производственной санитарии согласно требованиям санитарно-эпидемиологического режима, участвует в контроле качества проводимых в лаборатории исследований.</p>
<p>Медицинский лабораторный техник должен обладать аналитическими и техническими знаниями и умениями, активно применять современные информационные технологии, уметь распределять время и организовывать свою работу в условиях высокой эмоциональной нагрузки.</p>
<p>&nbsp;</p>
<p><strong>Название</strong><strong> ENG: Medical Analysis Services </strong></p>
<p><strong>Статус компетенции: Основная </strong></p>
<p><strong>Тип: RU </strong></p>
<p><strong>№ CIS: R2 </strong></p>
<p><strong>Количество конкурсантов в команде: 1</strong></p>', 4, 6, 24, 5, 4, 2)
